import React, { useEffect } from "react";
import { getRestaurants } from "../actions/restaurantAction";
import Restaurant from "../Components/Restaurant";
import Loader from "../Components/Layouts/Loader";
import Message from "../Components/Message";
import { useDispatch, useSelector } from "react-redux";
import CountRestaurant from "./CountRestaurant";
import {
  sortByRatings,
  sortByReviews,
  toggleVegOnly,
} from "../actions/restaurantAction";

const Home = () => {
  const dispatch = useDispatch();
  const {
    loading: restaurantsLoading,
    error: restaurantsError,
    restaurants,
    showVegOnly,
  } = useSelector((state) => state.restaurants);
  useEffect(() => {
    if (restaurantsError) {
      return alert.error(restaurantsError);
    }
    dispatch(getRestaurants());
  }, [dispatch, restaurantsError]);
  const handleSortByRatings = () => {
    dispatch(sortByRatings());
  };
  const handleSortByReviews = () => {
    dispatch(sortByReviews());
  };
  const handleSortByVegOnly = () => {
    dispatch(toggleVegOnly());
  };

  return (
    <>
    <CountRestaurant/>
      {restaurantsLoading ? (
        <Loader />
      ) : restaurantsError ? (
        <Message variant="danger">{restaurantsError}</Message>
      ) : (
        <>
          <section>
            <div className="sort">
              <button className="sort_veg p-3" onClick={handleSortByVegOnly}>
                {showVegOnly ? "Show All" : "Pure Veg"}
              </button>
              <button className="sort_rev p-3" onClick={handleSortByReviews}>
                Sort By Reviews
              </button>
              <button className="sort_rate p-3" onClick={handleSortByRatings}>
                Sort By Ratings
              </button>
            </div>
            <div className="row mt-4">
              {restaurants && restaurants.restaurants ? (
                restaurants.restaurants.map((restaurant) =>
                  !showVegOnly || (showVegOnly && restaurant.isVeg) ? (
                    <Restaurant key={restaurant._id} restaurant={restaurant} />
                  ) : null
                )
              ) : (
                <Message variant="info">No Restaurants Found</Message>
              )}
            </div>
          </section>
        </>
      )}
    </>
  );
};

export default Home;
